USE coe_ecommerce;

-- ============================================================
-- CATEGORIES
-- ============================================================
ON DUPLICATE KEY UPDATE
  name        = VALUES(name),
  description = VALUES(description);

-- ============================================================
-- PRERANA PRODUCTS (kebab-case filenames, no spaces)
-- ============================================================
ON DUPLICATE KEY UPDATE
  description   = VALUES(description),
  price         = VALUES(price),
  stock         = VALUES(stock),
  thumbnail_url = VALUES(thumbnail_url),
  is_active     = VALUES(is_active);

-- ============================================================
